import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FlightRm } from '../api/models';
import { FlightsService } from './../api/services/flights.service';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {


 

  constructor(private rout: ActivatedRoute, private flightService: FlightsService) { }
  flightID: string = "this is not loaded";
  ngOnInit(): void {
    this.rout.paramMap
      .subscribe(p => this.findFlight(p.get("flightId")));
  }
  flight: FlightRm = {};

  private findFlight=(flightId: string | null) =>{

    this.flightID = flightId ?? "this is not assinge";

    this.flightService.findFlights({ id: this.flightID }).subscribe(flight => this.flight=flight)

  }
}




<div class="container">
<div class="card text-dark mt-5 text-center">
  <div class="card-body" style="padding: 20px; background-color:#e5ecf26e">
    <h6 class="card-title">{{flight.airline}}</h6>
    <div class="card-title" style="color:#ff3939">
      {{flight.remainingNumberOfSeats}} remaining seats
    </div>

    <div class="d-grid text-center g-2">
      <div class="row">
        <div class="col">

          <div class="col m-3">
            <i class="text-primary fas fa-plane-departure" style="font-size:20px;"></i>
          </div>

          <div class="col">
            <i class="fa-solid fa-location-dot">
              {{flight.departure?.place}}
            </i>
          </div>

          <div class="col m-2">
            <i class="fa-regular"></i>
            {{flight.departure?.time | date }}
          </div>

          <div class="col">
            <i class="fa-regular fa-clock"></i>
            {{flight.departure?.time | date:'shortTime' }}
          </div>


        </div>

        <div class="col">
          <div style="display:flex;">
            <i class="fa-solid fa-circle" style="margin-top:50px; font-size:9px;"></i>
            <hr style="display:block; width: 100%; margin-top:55px;" />
            <i class="fas fa-plane" style="margin-top:48px;"></i>
          </div>

          <i class="fas fa-usd">{{flight.price}}</i>
          <!--<br />
          <a [routerLink]="['/book-flight',flight.id]" class="btn btn-secondary btn-lg mt-2">
            <i class="fas fa-ticket"></i>
            Book
          </a>-->
        </div>

        <div class="col">
          <div class="col m-3">
            <i class="text-primary fas fa-plane-arrival" style="font-size:20px;"></i>
          </div>

          <div class="col">
            <i class="fa-solid fa-location-dot">
              {{flight.arrival?.place}}
            </i>
          </div>

          <div class="col m-2">
            <i class="fa-regular"></i>
            {{flight.arrival?.time | date }}
          </div>

          <div class="col">
            <i class="fa-regular fa-clock"></i>
            {{flight.arrival?.time | date:'shortTime' }}
          </div>
        </div>


      </div>
    </div>

  </div>

</div>

</div>
<h4>
  This is flight

</h4>

<div>


  {{flight.airline}}
  {{flight.arrival?.place}}
  {{flight.arrival?.time | date:'shortTime'}}
  {{flight.departure?.place}}
  {{flight.arrival?.time | date:'shortTime'}}
  {{flight.price}}
  {{flight.remainingNumberOfSeats}}
</div>
