import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FlightRm } from '../api/models';
import { FlightsService } from './../api/services/flights.service';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent implements OnInit {


 

  constructor(private rout: ActivatedRoute, private flightService: FlightsService) { }
  flightID: string = "this is not loaded";
  ngOnInit(): void {
    this.rout.paramMap
      .subscribe(p => this.findFlight(p.get("flightId")));
  }
  flight: FlightRm = {};

  private findFlight=(flightId: string | null) =>{

    this.flightID = flightId ?? "this is not assinge";

    this.flightService.findFlights({ id: this.flightID }).subscribe(flight => this.flight=flight)

  }
}
