import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { AuthService } from '../Auth/auth.service';
//import { RequestBody } from 'ng-openapi-gen/lib/request-body';
import { PassengerService } from './../api/services/passenger.service';
import { Router, ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-register-passenger',
  templateUrl: './register-passenger.component.html',
  styleUrls: ['./register-passenger.component.css']
})
export class RegisterPassengerComponent  {

  constructor(private passengerService: PassengerService, private fb: FormBuilder
    , private authService: AuthService, private router: Router,

  ) {

  }


  form = this.fb.group({
    email: [''],
    firstName: [''],
    lastName: [''],
    gender: [true]
     
  });
 


  CheckPassanger(): void {

    const email = this.form.get("email")?.value ?? ''; // Use an empty string as a default value if email is null or undefined
    const parms = { email: email };

    this.passengerService.findPassangerPassenger(parms)
      .subscribe(this.login)

  }

  Register() {
    console.log(this.form.value);
    this.passengerService
      .registerPassenger({ body: this.form.value })
      .subscribe(this.login, e => {
        if (e.status != 404)
          console.error(e)
      } )
  }

  private login = () => {
    this.authService.loginUser({ email: this.form.get("email")?.value })
    this.router.navigate(['/search-flights'])
  }
}

<form [formGroup]="form">

  <div class="container d-grid gap-3" style="max-width: 700px;">

    <div class="form-floating">
      <input  (blur)="CheckPassanger()" formControlName="email" placeholder="Email" class="form-control" type="text" />
      <label for="email">Email</label>

      <div class="invalid-feedback d-block">
        <!--<div>
          Email is invalid
        </div>-->
      </div>

    </div>
    <div class="d-grid gap-3">

      <div class="form-floating">

        <input formControlName="firstName" class="form-control" type="text" id="firstName" placeholder="First Name" />
        <label class="form-label" for="firstName">First Name</label>

        <div class="invalid-feedback d-block">

          <!--<div>
            First name is invalid
          </div>-->
        </div>
      </div>

      <div class="form-floating">

        <input formControlName="lastName" placeholder="Last Name" class="form-control" id="lastName" type="text" />
        <label class="form-label" for="lastName">Last Name</label>

        <div class="invalid-feedback d-block">
          <!--<div>
            Last name is invalid
          </div>-->

        </div>
      </div>


      <div>

        <input formControlName="gender" id="gender" class="form-check-input" [value]=true type="radio" />
        <label class="ms-2 form-check-label" for="gender">Female</label>
      </div>
      <div>
        <input formControlName="gender" id="gender" class="form-check-input" [value]=false type="radio" />
        <label class="ms-2 form-check-label" for="gender">Male</label>
      </div>
    </div>
    <div class="d-flex justify-content-center center">
      <button (click)="Register()" type="submit" class="btn btn-primary">Login <i class="fa fa-sign-in" aria-hidden="true"></i></button>
    </div>
  </div>


</form>
